import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";

export default function Team() {
  const [teams, setTeams] = useState([]);
  var imageUrl = "";

  useEffect(() => {
    axios
      .get("https://wordpress.nsattu.com/wp-json/wp/v2/teams?per_page=20")
      .then((res) => {
        setTeams(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  const getImageUrl = (featured_media) => {
    axios
      .get(`https://wordpress.nsattu.com/wp-json/wp/v2/media/${featured_media}`)
      .then((res) => {
        // imageUrl = res.data.media_details.sizes.full.source_url;
        imageUrl = res.data.guid.rendered;
      })
      .catch((err) => console.log(err));
    return imageUrl;
  };

  return (
    <div className="container px-6 py-10 mx-auto">
      <h1 className="text-3xl font-semibold text-center text-gray-800 capitalize ">
        Our Executive Team
      </h1>
      <div className="grid grid-cols-3 gap-8 mt-8"></div>
      {teams.map((teams) => (
        <Fragment>
          {(imageUrl = getImageUrl(teams.featured_media))}
          <div className="flex flex-col items-center p-8 transition-colors duration-200 transform cursor-pointer group hover:bg-blue-600 rounded-xl">
            <img
              className="object-cover w-32 h-32 rounded-full ring-4 ring-gray-300"
              src={imageUrl}
              alt=""
            />
            {console.log(getImageUrl(teams.featured_media))}
          </div>
        </Fragment>
      ))}
    </div>
  );
}
